package com.munsellapp.munsellcolorrecognitionapp;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;public class SubmitForm extends AppCompatActivity {    TextView munsellValueText;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_submit_form);        Bundle bundle=getIntent().getExtras();        String munsellValueString = bundle.getString("MunsellValue");        munsellValueText=(TextView) findViewById(R.id.sfMunsellValue);        munsellValueText.setText(munsellValueString);    }}